import React, { useState } from"react";



const Contact=()=>{
const [data,setdata]=useState({
  fname:"",
  phone:"",
  email:""
})

const formsubmit=(e)=>{
e.preventDefault();
alert(`my name is ${data.fname}`);
};


const inputevent=(event)=>{
const{name,value}=event.target;
setdata((prevvalue)=>{
return{...prevvalue,[name]:value,}
})

}


    return(
        <>
        <div className="contact_pg">
        <h1>Contact Now</h1>
        
        <div className="container">
        <div className="row">
        <div className="col-6 mx-auto">
        <form onSubmit={formsubmit}>
  <div class="form-group" >
    <label for="formGroupExampleInput">Fullname</label>
    <input type="text" class="form-control" id="formGroupExampleInput" placeholder="Enter your name" 
      value={data.fname}
      name="fname"
      onChange={inputevent}
    />
  </div>
  <div class="form-group">
    <label for="formGroupExampleInput2">Email</label>
    <input type="text" class="form-control" id="formGroupExampleInput2" placeholder="Enter your email"
       value={data.email}
      name="email"
      onChange={inputevent}
    />
  </div>
  <div class="form-group">
    <label for="formGroupExampleInput2">Mobile No</label>
    <input type="text" class="form-control" id="formGroupExampleInput2" placeholder="Enter your mobile number"
      value={data.phone}
      name="phone"
      onChange={inputevent}
    />
  </div>
  <div class="col-auto">
      <button type="submit" class="btn btn-primary mb-2">Submit</button>
    </div>
</form>
</div>
</div>



        </div>
        </div>
      
  </>

    )

};

export default Contact;
